import React, { Component, PureComponent } from 'react';
import moment from 'moment';
import { ReactComponent as PlusIcon } from './add.svg';
import './App.css';

import Tasks from './Tasks';

class Task extends PureComponent {
    render() {
        const {task, toggleTask} = this.props;
        return (
            <div key={task.id} className="taskListItem" onClick={toggleTask(true, task)}>
                {task.name}
            </div>
        )
    }
}

class App extends Component {
  state = {
      taskText: '',
      newTasks: JSON.parse(localStorage.getItem('newTasks')) || [],
      completedTasks: [{name: 'Bla', date: moment(), id: moment().format('x')}],
  };

  addNewTask = () => {
      const {taskText, newTasks} = this.state;
      const task = {
          id: moment().format('x'),
          date: moment(),
          name: taskText
      };
      newTasks.push(task);
      this.setState({taskText: '', newTasks});
      localStorage.setItem('newTasks', JSON.stringify(newTasks));
  };

  changeTaskText = (e) => {
      this.setState({taskText: e.target.value});
  };

  toggleTask = (checked, task) => () => {
      const {newTasks, completedTasks} = this.state;
      if (checked) {
        completedTasks.push(task);
        const filteredNewTasks = newTasks.filter(newTask => newTask.id !== task.id);
          this.setState({newTasks: filteredNewTasks, completedTasks});
      } else {
          newTasks.push(task);
          const filteredCompletedTasks = completedTasks.filter(completedTask => completedTask.id !== task.id);
          this.setState({newTasks, completedTasks: filteredCompletedTasks});
      }
    // console.log(checked, i);
  };

  render() {
      const {taskText, newTasks, completedTasks} = this.state;
      console.log(newTasks, completedTasks);
      return (
      <div className="app">
          <div className="appWrapper">
            <h1 className="appTitle">Task Manager</h1>
              <div className="panelWrapper">
                  <h1 className="panelTitle">Work</h1>
                  <div className="taskInputWrapper">
                      <PlusIcon className="plusSign" />
                      <input
                          type="text"
                          className="taskInput"
                          placeholder="Add new task"
                          value={taskText}
                          onChange={this.changeTaskText}
                      />
                      <div className={`addBtn${taskText && ' active'}`} onClick={this.addNewTask}>Add
                          {/*<div className="blueBottomBorder" />*/}
                      </div>
                  </div>
                  <div className="newTasksWrapper">
                      {newTasks.map((task, i) =>
                          <Task key={i} task={task} toggleTask={this.toggleTask} />
                      )}

                      {/*<label className="taskListItem">BlaBla*/}
                      {/*<input type="checkbox" className="checkbox" onClick={this.toggleTask} />*/}
                      {/*<span className="checkmark" />*/}
                      {/*</label>*/}
                  </div>
                  <div className="completedTasksWrapper">
                      {completedTasks.map((task) => {
                          const date = moment(task.date).format('MMM D, YYYY');
                          return <div key={task.id} className="taskListItem completed" onClick={this.toggleTask(false, task)}>
                              <span>{task.name}</span>
                              <div className="completedDate">{date}</div>
                          </div>;
                      })}
                  </div>
                  {/*<Tasks newTasks={newTasks} completedTasks={completedTasks} toggleTask={this.toggleTask} />*/}
              </div>
          </div>
      </div>
    );
  }
}

export default App;
