import React, { Component } from 'react';
import { ReactComponent as PlusIcon } from './add.svg';
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
            taskText: ''
        };
    }

  changeTaskText = (e) => {
      this.setState({taskText: e.target.value});
  }

  render() {
    return (
      <div className="app">
          <div className="appWrapper">
            <h1 className="appTitle">Task Manager</h1>
              <div className="listWrapper">
                  <h1 className="listTitle">Work</h1>
                  <div className="taskInputWrapper">
                      <PlusIcon className="plusSign" />
                      <input
                          type="text"
                          className="taskInput"
                          placeholder="Add new task"
                          value={this.state.taskText}
                          onChange={this.changeTaskText}
                      />
                      <div className="addBtn">Add
                          {/*<div className="blueBottomBorder" />*/}
                      </div>
                  </div>
              </div>
          </div>
      </div>
    );
  }
}

export default App;
