import React, { Component } from 'react';
import moment from 'moment';
import { ReactComponent as PlusIcon } from './add.svg';
import './App.css';

import Tasks from './Tasks';

class App extends Component {
  state = {
      taskText: '',
      newTasks: JSON.parse(localStorage.getItem('newTasks')) || [],
      completedTasks: [{name: 'Bla', date: moment()}],
  };

  addNewTask = () => {
      const {taskText, newTasks} = this.state;
      const task = {
          date: moment(),
          name: taskText
      };
      newTasks.push(task);
      this.setState({taskText: '', newTasks});
      localStorage.setItem('newTasks', JSON.stringify(newTasks));
  };

  changeTaskText = (e) => {
      this.setState({taskText: e.target.value});
  };

  toggleTask = (checked, task, i) => () => {
      const {newTasks, completedTasks} = this.state;
      if (checked) {
          console.log('test');
        completedTasks.push(task);
        console.log(completedTasks);
        this.setState({completedTasks});
      } else {
          newTasks.push(task);
          this.setState({newTasks});
      }
    // console.log(checked, i);
  };

  render() {
      const {taskText, newTasks, completedTasks} = this.state;
      console.log(newTasks, completedTasks);
      return (
      <div className="app">
          <div className="appWrapper">
            <h1 className="appTitle">Task Manager</h1>
              <div className="panelWrapper">
                  <h1 className="panelTitle">Work</h1>
                  <div className="taskInputWrapper">
                      <PlusIcon className="plusSign" />
                      <input
                          type="text"
                          className="taskInput"
                          placeholder="Add new task"
                          value={taskText}
                          onChange={this.changeTaskText}
                      />
                      <div className={`addBtn${taskText && ' active'}`} onClick={this.addNewTask}>Add
                          {/*<div className="blueBottomBorder" />*/}
                      </div>
                  </div>
                  <Tasks newTasks={newTasks} completedTasks={completedTasks} toggleTask={this.toggleTask()} />
              </div>
          </div>
      </div>
    );
  }
}

export default App;
