//         return (
//             <div key={task.id} className="taskListItem" onClick={toggleTask(true, task)}>
//                 {task.name}
//             </div>
//         )
//     }
// }

export class Tasks extends PureComponent {
    render() {
        const {newTasks, completedTasks, toggleTask} = this.props;
        return (
            <div>
                <div className="newTasksWrapper">
                {newTasks.map((task) =>
                    <div key={task.id} className="taskListItem" onClick={toggleTask(true, task)}>
                        {task.name}
                    </div>
                )}

                {/*<label className="taskListItem">BlaBla*/}
                {/*<input type="checkbox" className="checkbox" onClick={this.toggleTask} />*/}
                {/*<span className="checkmark" />*/}
                {/*</label>*/}
            </div>
                <div className="completedTasksWrapper">
                    {completedTasks.map((task) => {
                        const date = moment(task.date).format('MMM D, YYYY');
                        return <div key={task.id} className="taskListItem completed" onClick={toggleTask(false, task)}>
                            <span>{task.name}</span>
                            <div className="completedDate">{date}</div>
                        </div>;
                    })}
                </div>
            </div>
        );
    }
}

export default Tasks;
