import React, { Component } from 'react';
import moment from 'moment';
import { ReactComponent as PlusIcon } from './add.svg';
import './App.css';

class App extends Component {
  state = {
      taskText: '',
      newTasks: localStorage.getItem('newTasks'),
      completedTasks: [],
  };

  addNewTask = () => {
      const {taskText} = this.state;
      this.setState({taskText: ''});
  };

  changeTaskText = (e) => {
      this.setState({taskText: e.target.value});
  };

  toggleTask = (checked) => () => {
    console.log(checked);
  };

  render() {
      const {taskText, newTasks} = this.state;
      return (
      <div className="app">
          <div className="appWrapper">
            <h1 className="appTitle">Task Manager</h1>
              <div className="panelWrapper">
                  <h1 className="panelTitle">Work</h1>
                  <div className="taskInputWrapper">
                      <PlusIcon className="plusSign" />
                      <input
                          type="text"
                          className="taskInput"
                          placeholder="Add new task"
                          value={taskText}
                          onChange={this.changeTaskText}
                      />
                      <div className={`addBtn${taskText && ' active'}`} onClick={this.addNewTask}>Add
                          {/*<div className="blueBottomBorder" />*/}
                      </div>
                  </div>
                  <div className="newTasksWrapper">
                      {newTasks.map(task =>
                          <div className="taskListItem" onClick={this.toggleTask(true)}>{task}</div>
                      )}

                      {/*<label className="taskListItem">BlaBla*/}
                          {/*<input type="checkbox" className="checkbox" onClick={this.toggleTask} />*/}
                          {/*<span className="checkmark" />*/}
                      {/*</label>*/}
                  </div>
                  <div className="completedTasksWrapper">
                      {completedTasks.map(task =>
                          <div className="taskListItem completed" onClick={this.toggleTask()}>
                              <span>{task}</span>
                          </div>
                      )}
                  </div>
              </div>
          </div>
      </div>
    );
  }
}

export default App;
